package com.ezreal.model;import lombok.Data;import java.util.Objects;/** * @author ezeal */@Datapublic class RetryModel<T> {    private Integer retryTimes;    private T t;    public void retryTimesPlus() {        if (Objects.isNull(retryTimes)) {            this.retryTimes = 0;        }        this.retryTimes++;    }    public void init(T t) {        this.t = t;        this.retryTimes = 0;    }}